INSERT INTO genres (name, user_id, description, added) VALUES ('Chillout', 0, 'Жанр электронной музыки, название которого произошло от английского сленгового to chill out — остынь, расслабься, который объединяет в себе спокойную музыку разных направлений и стилей.', now());
