INSERT INTO genres (name, user_id, description, added) VALUES ('Indie-Rock', 0, 'Oбобщающий термин для широкого диапазона музыкантов и стилей, объединённых причастностью к контркультуре и имеющих некоторое отношение к рок-музыке. Изначально термин «инди» использовался для обозначения независимых музыкальных лейблов, но впоследствии стал употребляться по отношению к музыке, выпускавшейся на этих лейблах. В 1980-х термин инди-рок был синонимом альтернативного рока и применялся для разделения исполнителей по географическому признаку: при этом альтернативный рок был предпочтительным для американских исполнителей, а инди-рок — для британских. С попаданием американских гранж- и поп-панк-исполнителей, а затем и британских брит-поп-исполнителей в мейнстрим в 1990-х, термин стал использоваться для представителей андеграунда альтернативного рока. В 2000-х годах в результате изменений в музыкальной индустрии и растущего значения Интернета инди-рок приобрёл коммерческий успех, что привело к вопросам осмысленности термина.', now());
