INSERT INTO genres (name, user_id, description, added) VALUES ('Latino', 0, 'Oбобщённое название музыкальных стилей и жанров стран Латинской Америки, а также музыка выходцев из этих стран, компактно проживающих на территории других государств и образующих большие латиноамериканские сообщества (например, в США).', now());
