INSERT INTO genres (name, user_id, description, added) VALUES ('Techno', 0, 'Cтиль электронной музыки, зародившийся в середине 1980-х годов в Детройте и его окрестностях и впоследствии подхваченный европейскими продюсерами. Он характеризуется искусственностью звука, акцентом на механических ритмах, многократным повторением структурных элементов музыкального произведения. Является одним из основных музыкальных жанров, исполняемых на рейвах.', now());
